import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';

const root = ReactDOM.createRoot(document.getElementById('root'));

let scrollHandler = function (e) {
  let up = this.oldScroll > this.scrollY;
  this.oldScroll = this.scrollY;

  if (window.scrollY >= window.innerHeight/100 && window.scrollY < window.innerHeight && !up) {
    window.scroll({
      top: window.innerHeight*0.99,
      left: 0,
      behavior: 'smooth'
    })
  } else if (window.scrollY <= window.innerHeight*0.99 && up) {
    window.scroll({
      top: -window.scrollY,
      left: 0,
      behavior: 'smooth'
    })
  }
};

window.onscroll = scrollHandler

root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
